import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav } from "react-bootstrap";
import { ReactComponent as AboutIcon } from './assets/img/about.svg';
import { ReactComponent as PortfolioIcon } from './assets/img/portfolio.svg';
import { ReactComponent as ContactIcon } from './assets/img/contact.svg';
import { ReactComponent as ResumeIcon } from './assets/img/resume.svg';

const PortfolioNavbar = () => {
    const [activeLink, setActiveLink] = useState('about');
    const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

    useEffect(() => {
        const updateMedia = () => {
            setIsMobile(window.innerWidth < 768);
        };

        window.addEventListener('resize', updateMedia);
        updateMedia();  // Call once to set initial state based on current viewport
        return () => window.removeEventListener('resize', updateMedia);
    }, []);

    return (
        <>
        {isMobile ? (
            <Navbar fixed="bottom" expand="lg" className="bg-dark rounded-top-nav" style={{height: '60px'}}>
                <Nav className="w-100 justify-content-around">
                    <Nav.Item>
                        <Nav.Link href="#about" onClick={() => setActiveLink('about')} className={activeLink === 'about' ? "active" : ""}>
                            <AboutIcon />
                            <div>About Me</div>
                        </Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link href="#portfolio" onClick={() => setActiveLink('portfolio')} className={activeLink === 'portfolio' ? "active" : ""}>
                            <PortfolioIcon />
                            <div>Portfolio</div>
                        </Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link href="#contact" onClick={() => setActiveLink('contact')} className={activeLink === 'contact' ? "active" : ""}>
                            <ContactIcon />
                            <div>Contact</div>
                        </Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link href="#resume" onClick={() => setActiveLink('resume')} className={activeLink === 'resume' ? "active" : ""}>
                            <ResumeIcon />
                            <div>Resume</div>
                        </Nav.Link>
                    </Nav.Item>
                </Nav>
            </Navbar>
        ) : (
            // Default Navbar here for non-mobile
            <Navbar expand="lg" className="bg-light">
                {/* Place your regular navbar content here */}
            </Navbar>
        )}
        </>
    );
}

export default PortfolioNavbar;
