import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav } from "react-bootstrap";
import { ReactComponent as AboutIcon } from './assets/img/about.svg';
import { ReactComponent as PortfolioIcon } from './assets/img/portfolio.svg';
import { ReactComponent as ContactIcon } from './assets/img/contact.svg';
import { ReactComponent as ResumeIcon } from './assets/img/resume.svg';

const PortfolioNavbar = () => {
    const [activeLink, setActiveLink] = useState('about');

    useEffect(() => {
        const onResize = () => {
            // Add any responsive adjustments here if needed
        };
        window.addEventListener("resize", onResize);

        return () => {
            window.removeEventListener("resize", onResize);
        };
    }, []);

    return (
        <Navbar className="fixed-bottom bg-dark rounded-top-nav" style={{height: '60px'}}>
            <Nav className="w-100 justify-content-around">
                <Nav.Item>
                    <Nav.Link href="#about" onClick={() => setActiveLink('about')} className={activeLink === 'about' ? "active" : ""}>
                        <AboutIcon />
                        <div>About Me</div>
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link href="#portfolio" onClick={() => setActiveLink('portfolio')} className={activeLink === 'portfolio' ? "active" : ""}>
                        <PortfolioIcon />
                        <div>Portfolio</div>
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link href="#contact" onClick={() => setActiveLink('contact')} className={activeLink === 'contact' ? "active" : ""}>
                        <ContactIcon />
                        <div>Contact</div>
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link href="#resume" onClick={() => setActiveLink('resume')} className={activeLink === 'resume' ? "active" : ""}>
                        <ResumeIcon />
                        <div>Resume</div>
                    </Nav.Link>
                </Nav.Item>
            </Nav>
        </Navbar>
    );
}

export default PortfolioNavbar;
