import React, { useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { Container, Row } from 'react-bootstrap';
import "../App.css";

export const Portfolio = () => {
    useEffect(() => {
        AOS.init({
          duration : 1000 // Can be adjusted for different speeds
        });
    }, []);

    return (
        <Row id="portfolio">
            <Container className="d-flex flex-column" id="projects">
                <div className="proj d-flex justify-content-center flex-column">
                    {/* Initial element with standard offset */}
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="300"><a href="https://github.com/marphco/pennywise" target="_blank" rel="noopener noreferrer">PENNYWISE</a></div>
                    {/* Increasing offsets for subsequent elements */}
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="400">NOTEVERSE</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="500">ART HISTORY</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="600">NOTEVERSE</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="700">README BOT</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="800">FLEXI SOCIAL API</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="900">INFINI CART</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="1000">EMPLOYEE SYNC</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="1100">SCRIBELLA</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="1200">SVG FORGE</div>
                    <div className="proj-title" data-aos="fade-up" data-aos-offset="1300">SKY SWAY</div>
                </div>
            </Container>
        </Row>
    );
}

export default Portfolio;
