import React, { useEffect, useState } from 'react';
import { Container, Row } from 'react-bootstrap';

export const Portfolio = () => {
    const [activeProject, setActiveProject] = useState(0);
    const projects = ['PENNYWISE', 'NOTEVERSE', 'ART HISTORY', 'README BOT', 'FLEXI SOCIAL API', 'INFINI CART'];

    useEffect(() => {
        const handleScroll = (e) => {
            // Prevent default scrolling
            e.preventDefault();
            const scrollDelta = e.deltaY;

            // Scroll handling logic
            if (scrollDelta > 0 && activeProject < projects.length - 1) {
                setActiveProject(activeProject + 1);
            } else if (scrollDelta < 0 && activeProject > 0) {
                setActiveProject(activeProject - 1);
            }
        };

        // Add event listener
        document.getElementById('portfolio').addEventListener('wheel', handleScroll);

        return () => {
            // Clean up event listener
            document.getElementById('portfolio').removeEventListener('wheel', handleScroll);
        };
    }, [activeProject, projects.length]);

    return (
        <Row id="portfolio">
            <Container>
                {projects.map((proj, index) => (
                    <div key={index} style={{ height: '100vh', display: index === activeProject ? 'block' : 'none' }}>
                        {proj}
                    </div>
                ))}
            </Container>
        </Row>
    );
};

export default Portfolio;
