import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav } from "react-bootstrap";
import { ReactComponent as AboutIcon } from './assets/icons/about.svg';
import { ReactComponent as PortfolioIcon } from './assets/icons/portfolio.svg';
import { ReactComponent as ContactIcon } from './assets/icons/contact.svg';
import { ReactComponent as ResumeIcon } from './assets/icons/resume.svg';

const PortfolioNavbar = () => {
    const [activeLink, setActiveLink] = useState('about');
    const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

    useEffect(() => {
        const updateMedia = () => {
            setIsMobile(window.innerWidth < 768);
        };

        window.addEventListener('resize', updateMedia);
        return () => window.removeEventListener('resize', updateMedia);
    }, []);

    return (
        <Navbar expand="lg" className={`${isMobile ? "fixed-bottom bg-dark rounded-top-nav" : "bg-light"}`} style={isMobile ? {height: '60px'} : {}}>
            <Nav className="w-100 justify-content-around">
                {isMobile ? (
                    <>
                        <Nav.Item>
                            <Nav.Link href="#about" onClick={() => setActiveLink('about')} className={activeLink === 'about' ? "active" : ""}>
                                <AboutIcon />
                                <div>About Me</div>
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link href="#portfolio" onClick={() => setActiveLink('portfolio')} className={activeLink === 'portfolio' ? "active" : ""}>
                                <PortfolioIcon />
                                <div>Portfolio</div>
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link href="#contact" onClick={() => setActiveLink('contact')} className={activeLink === 'contact' ? "active" : ""}>
                                <ContactIcon />
                                <div>Contact</div>
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link href="#resume" onClick={() => setActiveLink('resume')} className={activeLink === 'resume' ? "active" : ""}>
                                <ResumeIcon />
                                <div>Resume</div>
                            </Nav.Link>
                        </Nav.Item>
                    </>
                ) : (
                    // Desktop Navbar content here
                    <Nav className="me-auto">
                        <Nav.Link href="#about" onClick={() => setActiveLink('about')} className={activeLink === 'about' ? "active" : ""}>About Me</Nav.Link>
                        <Nav.Link href="#portfolio" onClick={() => setActiveLink('portfolio')} className={activeLink === 'portfolio' ? "active" : ""}>Portfolio</Nav.Link>
                        <Nav.Link href="#contact" onClick={() => setActiveLink('contact')} className={activeLink === 'contact' ? "active" : ""}>Contact</Nav.Link>
                        <Nav.Link href="#resume" onClick={() => setActiveLink('resume')} className={activeLink === 'resume' ? "active" : ""}>Resume</Nav.Link>
                    </Nav>
                )}
            </Nav>
        </Navbar>
    );
}

export default PortfolioNavbar;
