// import Button from 'react-bootstrap/Button';
// import Container from 'react-bootstrap/Container';
// import Form from 'react-bootstrap/Form';
// import Nav from 'react-bootstrap/Nav';
// import Navbar from 'react-bootstrap/Navbar';
// import NavDropdown from 'react-bootstrap/NavDropdown';

import { useState, useEffect } from "react"
import { Navbar, Button, Container, Form, Nav, NavDropdown } from "react-bootstrap"
import logo from '../assets/img/logo.svg'
import linkedinIcon from '../assets/img/linkedin.svg'

export const PortfolioNavbar = () => {
    const { activeLink, setActiveLink } = useState('home')
    const { scrolled, seScrolled } = useState(false)

    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                seScrolled(true)
            } else {
                seScrolled(false)
            }
        }

        window.addEventListener("scroll", onScroll)

        return () => window.removeEventListener("scroll", onScroll)
        }, [])

        const onUpdateActiveLink = (value) => {
            setActiveLink(value)
        }

  return (
    <Navbar expand="lg" className={scrolled ? "scrolled" : ""}>
      <Container fluid>
        <Navbar.Brand href="#home">
            <img src={ logo } alt="Logo" />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />

        <span className="navbar-toggler-icon"></span>

        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#home" classnName={activeLink === "home" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("home")}>Home</Nav.Link>
            <Nav.Link href="#skills" classnName={activeLink === "skills" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("skills")}>Skills</Nav.Link>
            <Nav.Link href="#projects" classnName={activeLink === "projects" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("projects")}>Projects</Nav.Link>
            <Nav.Link href="#Contacts" classnName={activeLink === "contacts" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("contacts")}>Contacts</Nav.Link>
            <NavDropdown title="Link" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action4">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action5">
                Something else here
              </NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="#" disabled>
              Link
            </Nav.Link>
          </Nav>

          <span className="navbar-text">
            <div className="social-icon">
                <a href="#linkedin"><img src={ linkedinIcon } alt="Linkedin"/></a>
            </div>
          </span>

        <button className="button" onClick={() => console.log('connect')}><span>Let's Connect!</span></button>

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
