import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import AboutIcon from "./assets/img/about.svg"; // Correct paths assumed
import PortfolioIcon from "./assets/img/portfolio.svg";
import ContactIcon from "./assets/img/contact.svg";
import ResumeIcon from "./assets/img/resume.svg";
import logo from "../logo.svg";
import linkedinIcon from "./assets/img/linkedin.svg";
import githubIcon from "./assets/img/github.svg";

const PortfolioNavbar = () => {
  const [activeLink, setActiveLink] = useState("home");
  const [scrolled, setScrolled] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    const updateMedia = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener("scroll", onScroll);
    window.addEventListener("resize", updateMedia);
    updateMedia(); // Set initial state based on current viewport

    return () => {
      window.removeEventListener("scroll", onScroll);
      window.removeEventListener("resize", updateMedia);
    };
  }, []);

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  };

  return (
    <>
      {isMobile ? (
        <Navbar fixed="bottom" expand="lg" className="mobile-navbar">
          <Nav className="w-100 d-flex justify-content-center flex-row navbar-nav">
            <Nav.Item className="nav-item justify-content-around">
              <Nav.Link
                href="#about"
                className={activeLink === "about" ? "active" : ""}
                onClick={() => setActiveLink("about")}
              >
                <img src={AboutIcon} alt="About Me" />
                <div>About Me</div>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item className="nav-item justify-content-around">
              <Nav.Link
                href="#portfolio"
                className={activeLink === "portfolio" ? "active" : ""}
                onClick={() => setActiveLink("portfolio")}
              >
                <img src={PortfolioIcon} alt="Portfolio" />
                <div>Portfolio</div>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item className="nav-item justify-content-around">
              <Nav.Link
                href="#contact"
                className={activeLink === "contact" ? "active" : ""}
                onClick={() => setActiveLink("contact")}
              >
                <img src={ContactIcon} alt="Contact" />
                <div>Contact</div>
              </Nav.Link>
            </Nav.Item>
            <Nav.Item className="nav-item justify-content-around">
              <Nav.Link
                href="#resume"
                className={activeLink === "resume" ? "active" : ""}
                onClick={() => setActiveLink("resume")}
              >
                <img src={ResumeIcon} alt="Resume" />
                <div>Resume</div>
              </Nav.Link>
            </Nav.Item>
          </Nav>
        </Navbar>
      ) : (
        <Navbar expand="lg" className={scrolled ? "navbar scrolled" : "navbar"}>
    <Container fluid>
      <Navbar.Collapse id="navbarScroll">
        <Nav className="mx-auto my-2 my-lg-0" style={{ maxHeight: "100px" }} navbarScroll>
          <Nav.Link href="#home" className={activeLink === "home" ? "active" : ""} onClick={() => onUpdateActiveLink("home")}>Home</Nav.Link>
          <Nav.Link href="#about" className={activeLink === "about" ? "active" : ""} onClick={() => onUpdateActiveLink("about")}>About Me</Nav.Link>
          <Nav.Link href="#portfolio" className={activeLink === "portfolio" ? "active" : ""} onClick={() => onUpdateActiveLink("portfolio")}>Portfolio</Nav.Link>
          <Nav.Link href="#contacts" className={activeLink === "contacts" ? "active" : ""} onClick={() => onUpdateActiveLink("contacts")}>Contacts</Nav.Link>
          <Nav.Link href="#resume" className={activeLink === "resume" ? "active" : ""} onClick={() => onUpdateActiveLink("resume")}>Resume</Nav.Link>
        </Nav>
        <div className="social-icon">
          <a href="#linkedin"><img src={linkedinIcon} alt="LinkedIn" /></a>
          <a href="#github"><img src={githubIcon} alt="GitHub" /></a>
        </div>
      </Navbar.Collapse>
    </Container>
  </Navbar>
      )}
    </>
  );
};

export default PortfolioNavbar;
