import React, { useState, useEffect } from 'react';
import { Container, Row } from 'react-bootstrap';
import AOS from 'aos';
import 'aos/dist/aos.css';

const projects = [
  "PENNYWISE", "NOTEVERSE", "ART HISTORY", "README BOT", "FLEXI SOCIAL API",
  "INFINI CART", "EMPLOYEE SYNC", "SCRIBELLA", "SVG FORGE", "SKY SWAY"
];

export const Portfolio = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    useEffect(() => {
        AOS.init({ duration: 1000 });

        const handleScroll = (event) => {
            event.preventDefault();
            const { scrollTop, clientHeight, scrollHeight } = event.currentTarget;

            // Check if we've scrolled to the bottom of the current active project title
            if (scrollTop + clientHeight >= scrollHeight && activeIndex < projects.length - 1) {
                setActiveIndex(prevIndex => prevIndex + 1); // Reveal next project title
            }
        };

        const container = document.getElementById('portfolio');
        container.addEventListener('scroll', handleScroll);

        return () => {
            container.removeEventListener('scroll', handleScroll);
        };
    }, [activeIndex]);

    return (
        <Row id="portfolio" style={{ height: '100vh', overflow: 'auto' }}>
            <Container>
                {projects.map((project, index) => (
                    <div key={index} className="proj-title" style={{ display: index <= activeIndex ? 'block' : 'none' }}>
                        {project}
                    </div>
                ))}
            </Container>
        </Row>
    );
};

export default Portfolio;
