// import Button from 'react-bootstrap/Button';
// import Container from 'react-bootstrap/Container';
// import Form from 'react-bootstrap/Form';
// import Nav from 'react-bootstrap/Nav';
// import Navbar from 'react-bootstrap/Navbar';
// import NavDropdown from 'react-bootstrap/NavDropdown';

import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Button, Container, Form, Nav, NavDropdown } from "react-bootstrap"
import logo from '../logo.svg';
import linkedinIcon from './assets/img/linkedin.svg'

const PortfolioNavbar = () => {
    const [ activeLink, setActiveLink ] = useState('home')
    const [ scrolled, setScrolled ] = useState(false)

    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true)
            } else {
                setScrolled(false)
            }
        }

        window.addEventListener("scroll", onScroll)

        return () => window.removeEventListener("scroll", onScroll)
        }, [])

        const onUpdateActiveLink = (value) => {
            setActiveLink(value)
        }

  return (
    <Navbar expand="lg" className={scrolled ? "scrolled" : "fixed-bottom"}>
      <Container fluid>
        <Navbar.Brand href="#home">
            <img src={ logo } alt="Logo" className="logo"/>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />

        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#home" className={activeLink === "home" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("home")}>Home</Nav.Link>
            <Nav.Link href="#skills" className={activeLink === "skills" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("skills")}>Skills</Nav.Link>
            <Nav.Link href="#projects" className={activeLink === "projects" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("projects")}>Projects</Nav.Link>
            <Nav.Link href="#Contacts" className={activeLink === "contacts" ? "active navbar-link" : "navbar-link"} onClick={ () => onUpdateActiveLink("contacts")}>Contacts</Nav.Link>
          </Nav>

          <span className="navbar-text">
            <div className="social-icon">
                <a href="#linkedin"><img src={ linkedinIcon } alt="Linkedin"/></a>
            </div>
          </span>

        <button className="button" onClick={() => console.log('connect')}><span>Let's Connect!</span></button>

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default PortfolioNavbar;
